function c(){const r=document.querySelectorAll(".plus"),o=document.querySelectorAll(".minus");document.querySelectorAll(".product"),r.forEach(n=>{n.addEventListener("click",u=>{const t=u.target.previousElementSibling,l=t.getAttribute("max"),e=parseInt(t.value);e<parseInt(l)&&(t.value=e+1)})}),o.forEach(n=>{n.addEventListener("click",u=>{const t=u.target.nextElementSibling,l=t.getAttribute("min"),e=parseInt(t.value);e>parseInt(l)&&(t.value=e-1)})})}document.addEventListener("DOMContentLoaded",c);
